import pygamefrom boat import Boatfrom polar import polar_functionfrom buoy import Buoyfrom constants import *vrPolar = polar_function("polar.pol")objective = [300, 200]boat = Boat('1', WIDTH - 800, HEIGHT - 500, 45, vrPolar)buoy = Buoy(objective[0], objective[1])pygame.init()screen = pygame.display.set_mode((WIDTH, HEIGHT))screen.fill((0, 128, 255))done = Falseclock = pygame.time.Clock()while not done:    for event in pygame.event.get():        if event.type == pygame.QUIT:            done = True    pressed = pygame.key.get_pressed()    if pressed[pygame.K_LEFT]:        boat.turn(1, LEFT)    if pressed[pygame.K_RIGHT]:        boat.turn(1, RIGHT)    if boat.is_arrived([WIDTH-objective[0], HEIGHT-objective[1]]):        done = True        print(f'Boat {boat.name} has arrived at the objective')    screen.fill((0, 128, 255))    screen.blit(boat.surf, boat.rect.topleft)    screen.blit(buoy.surf, [objective[0] - 15, objective[1] - 15])    pygame.display.flip()    boat.update()    if pressed[pygame.K_ESCAPE]:        done = True        print('Game escaped')    clock.tick(40)pygame.quit()print("Game terminated")